import xxsurajbxx.game.Input;import xxsurajbxx.game.Projectile;import java.awt.event.KeyEvent;import java.awt.event.MouseEvent;import java.lang.Math;public class Inpt extends Input {    public void handleInput() {        if((keys[KeyEvent.VK_W]==true||keys[KeyEvent.VK_UP]==true)&&xxTopDownShooter.player.y-xxTopDownShooter.player.height>0) {            xxTopDownShooter.player.y-=5;        }        if((keys[KeyEvent.VK_A]==true||keys[KeyEvent.VK_LEFT]==true)&&xxTopDownShooter.player.x-xxTopDownShooter.player.width>0) {            xxTopDownShooter.player.x-=5;        }        if((keys[KeyEvent.VK_S]==true||keys[KeyEvent.VK_DOWN]==true)&&xxTopDownShooter.player.y+xxTopDownShooter.player.height<xxTopDownShooter.HEIGHT-15) {            xxTopDownShooter.player.y+=5;        }        if((keys[KeyEvent.VK_D]==true||keys[KeyEvent.VK_RIGHT]==true)&&xxTopDownShooter.player.x+xxTopDownShooter.player.width<xxTopDownShooter.WIDTH-10) {            xxTopDownShooter.player.x+=5;        }        double distance = Math.sqrt(Math.pow(xxTopDownShooter.mx-xxTopDownShooter.player.x,2)+Math.pow(xxTopDownShooter.my-xxTopDownShooter.player.y,2));        xxTopDownShooter.player.angle = (Math.acos(Math.abs(xxTopDownShooter.player.x-xxTopDownShooter.mx)/distance)+Math.asin(Math.abs(xxTopDownShooter.player.y-xxTopDownShooter.my)/distance))/2;        if(xxTopDownShooter.mx>xxTopDownShooter.player.x&&xxTopDownShooter.my<xxTopDownShooter.player.y) {            xxTopDownShooter.player.angle = (2*Math.PI*0.25)-xxTopDownShooter.player.angle;        }        else if(xxTopDownShooter.mx>xxTopDownShooter.player.x&&xxTopDownShooter.my>xxTopDownShooter.player.y) {            xxTopDownShooter.player.angle += (2*Math.PI*0.25);        }        else if(xxTopDownShooter.mx<xxTopDownShooter.player.x&&xxTopDownShooter.my>xxTopDownShooter.player.y) {            xxTopDownShooter.player.angle = (2*Math.PI*0.75)-xxTopDownShooter.player.angle;        }        else if(xxTopDownShooter.mx<xxTopDownShooter.player.x&&xxTopDownShooter.my<xxTopDownShooter.player.y) {            xxTopDownShooter.player.angle += (2*Math.PI*0.75);        }    }    public void mouseMoved(MouseEvent mouse) {        xxTopDownShooter.mx = mouse.getX()-5;        xxTopDownShooter.my = mouse.getY()-30;    }    public void mouseClicked(MouseEvent mouse) {        Projectile.addProjectile(xxTopDownShooter.player.x, xxTopDownShooter.player.y, 10, 500, xxTopDownShooter.player.angle);    }}