import xxsurajbxx.game.GamePanel;import xxsurajbxx.game.Map;import xxsurajbxx.game.Projectile;import xxsurajbxx.game.Entity;import java.awt.Color;import java.awt.BasicStroke;import java.awt.Stroke;import java.awt.Graphics;import java.awt.Graphics2D;public class Panel extends GamePanel {    public void paintComponent(Graphics g) {        super.paintComponent(g);        xxTopDownShooter.map.displayMap(g, xxTopDownShooter.th, 0, 0);        xxTopDownShooter.player.drawEntity(g);        Entity.drawEntities(g);        Projectile.update(g);        drawCrosshairs(g);        super.drawFPS(g);    }    private void drawCrosshairs(Graphics g) {        Graphics2D g2 = (Graphics2D)g;        Stroke def = g2.getStroke();        g2.setStroke(new BasicStroke(4));        g2.setColor(Color.CYAN);        g2.drawLine(xxTopDownShooter.mx, xxTopDownShooter.my+5, xxTopDownShooter.mx, xxTopDownShooter.my+10);        g2.drawLine(xxTopDownShooter.mx-5, xxTopDownShooter.my, xxTopDownShooter.mx-10, xxTopDownShooter.my);        g2.drawLine(xxTopDownShooter.mx+5, xxTopDownShooter.my, xxTopDownShooter.mx+10, xxTopDownShooter.my);        g2.drawLine(xxTopDownShooter.mx, xxTopDownShooter.my-5, xxTopDownShooter.mx, xxTopDownShooter.my-10);        g2.setStroke(def);    }}