import xxsurajbxx.game.*;import javax.imageio.ImageIO;import java.io.IOException;import java.io.File;public class xxTopDownShooter extends Game {    public static int mx, my;    public static Entity player;    public static Entity enemy;    public static TileHandler th;    public static Map map;    static {        mx=0;        my=0;        player = new Entity("Player.png", 100, 1550/2, 825/2, 32, 32, 32, 32);        Entity.addEntity("Spider.png", 100, 1550/2, (825/2)-100, 32, 32, 32, 32);        th = new TileHandler(2);        th.addTile("TestTile.PNG", 32);th.addTile("TestTile1.PNG", 32);        map = new Map(46, 24, "Map.txt");    }    public Inpt input;    public Panel panel;    public GameFrame frame;    public static void main(String args[]) {        xxTopDownShooter a = new xxTopDownShooter(1550, 825, "xxTopDownShooter", 30);    }    public xxTopDownShooter(int x, int y, String t, int fps) {        super(x,y,t,fps);        input = new Inpt();        panel = new Panel();        frame = new GameFrame(x, y, t, panel, input);        start();    }    public void update() {        input.handleInput();        Entity.rotateEntities(player.x, player.y);        panel.repaint();        FramesPerSecond.addFrame();    }}